function getDiff(angle) {
    if (angle % 4 == 0) {
        return [1, 0]
    } else if (angle % 4 == 1) {
        return [0, -1]
    } else if (angle % 4 == 2) {
        return [-1, 0]
    } else if (angle % 4 == 3) {
        return [0, 1]
    }
}

class Direction {
    constructor(angle) {
        this.angle = angle;
        this.updateDiff();
    }

    updateDiff() {
        var diff = getDiff(this.angle);
        console.log(this.angle)
        this.dx = diff[0]
        this.dy = diff[1]
    }

    turnLeft(n) {
        this.angle = (this.angle + n) % 4;
        this.updateDiff();
    }

    turnRight(n) {
        this.angle = (this.angle + 3 * n) % 4;
        this.updateDiff();
    }

    turn(n) {
        if (n > 0) {
            this.turnLeft(n)
        } else {
            this.turnRight(n)
        }
    }
}

export default Direction;
