import React, { useState, useEffect } from "react"
import GameState from './game/GameState';

const DEFAULT_BOARD_SIZE = 16;
const DEFAULT_RENDER_SIZE = 800;
const FRAMERATE = 30;

const GAME_DISPLAY_STYLE = {
    width: DEFAULT_RENDER_SIZE.toString() + "px",
    height: DEFAULT_RENDER_SIZE.toString() + "px",
}


const GameDisplay = (props) => {
    const boardSize = props.boardSize == undefined ? DEFAULT_BOARD_SIZE : props.boardSize;
    const [gameState, setGameState] = useState(GameState(boardSize));
    const [gameTimer, setGameTimer] = useState(0);

    function update() {
        setGameState(gameState.update());
        setGameTimer(gameTimer + 1);
    }

