import React, { useState, useEffect } from 'react';

import { default as GameGraphics } from'./gameGraphics';
import { default as GameState } from './gameState';

const GameCanvas = (props) => {

    const [gameState, setGameState] = useState(new GameState());
    const [canvasTimer, setCanvasTimer] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setGameState(gameState.update(1));
            setCanvasTimer(canvasTimer + 1);
        }, 20);
        return () => clearInterval(interval);
      }, [canvasTimer]);

    console.log("hit");
    return (
        <canvas id="gameGraphics">
            <GameGraphics gameState={gameState} />
        </canvas>
    );
};

export default GameCanvas;
