import React, { useState, useEffect } from 'react';

import GameGraphics from'./GameGraphics';
import GameState from './GameState';
import GameController from './GameController';

const GameCanvas = (props) => {

    // Initial GameState
    const [gameState, setGameState] = useState(new GameState({

    }));
    const [canvasTimer, setCanvasTimer] = useState(0);

    // Keypress logic
    const [gameController, setGameController] = useState(new GameController({}));
    const handleKeypress = (event) => {
        console.log(event);
        gameController.handleKeypress(event.key);
        gameState.addAction(gameController.consumeKeypress());
    }

    // Main update loop
    useEffect(() => {
        const interval = setInterval(() => {
            setGameState(gameState.update(1));
            setCanvasTimer(canvasTimer + 1);
        }, 20);
        return () => clearInterval(interval);
      }, [canvasTimer]);

    // Render Output
    return (
        <div id="gameCanvas" tabIndex={0} onKeyDown={handleKeypress}>
            <canvas id="gameGraphics">
                <GameGraphics gameState={gameState} />
            </canvas>
        </div>
    );
};

export default GameCanvas;
