const ActionType = {
    MOVE: 0,
    PLACE: 1,
}

class GameAction {
    constructor(type, props) {
        this.type = type
        this.props = props
    }
}

class GameController {
    constructor({}) {
        this.actionQueue = [];
    }

    handleKeypress(key) {
        var action = null;
        if (key == 'w') {
            action = new GameAction(ActionType.MOVE, {dx: 0, dy: -1})
        } else if (key == 'a') {
            action = new GameAction(ActionType.MOVE, {dx: -1, dy: 0})
        } else if (key == 's') {
            action = new GameAction(ActionType.MOVE, {dx: 0, dy: 1})
        } else if (key == 'd') {
            action = new GameAction(ActionType.MOVE, {dx: 1, dy: 0})
        } else if (key == ' ') {
            action = new GameAction(ActionType.PLACE, {})
        }
        if (action != null) {
            this.actionQueue.push(action);
        }
        return this;
    }

    consumeAction() {
        return this.actionQueue.shift();
    }
}

export default GameController;
