import Direction from "./Direction";

// Placeholder for more complex mechanisms
class Cell {
    constructor(type, id) {
        this.type = type
    }
}

// Generate a random integer from a up to but not including b
function randint(a, b) {
    return Math.floor(Math.random() * (b - a)) + a;
}

// Generate a unique int of coordinate x, y given a maximum groupSize, in order
// to create a set of unique x, y values
function getPID(x, y, groupSize) {
    return x * groupSize + y;
}

const BOUNDARY_MARGIN = 4;

class Piece {
    constructor({center_x, center_y, angle, pieceSize, boardXSize, boardYSize, pidSize}) {
        [this.cx, this.cy] = [center_x, center_y];

        this.angle = angle;
        this.dxn = new Direction(angle);
        this.collisionFlag = false;
        this.pidSize = pidSize;

        // Create a set of actual cells, to be offset by cx and cy
        this.cells = new Map();
        var [x, y, dxn] = [0, 0, new Direction(randint(0, 4))];
        var pid = getPID(x, y, this.pidSize);
        for (var i = 0; i < pieceSize; i++) {
            var valid = false;
            while (!valid) {
                this.cells.set(pid, [x, y])
                var turnAngle = randint(-1, 2)
                dxn.turn(turnAngle)
                x += dxn.dx
                y += dxn.dy
                pid = getPID(x, y, this.pidSize)

                if (this.cells.get(pid) == undefined) {
                    valid = true
                } else {
                    valid = false
                    x -= dxn.dx
                    y -= dxn.dy
                    dxn.turn(-turnAngle)
                }
            }
        }

        // Create 4 different sets to check hitboxes in different gravity
        this.collisionSets = [];
        dxn = new Direction(0);
        var [x, y] = [0, 0]
        for (var angle = 0; angle < 4; angle++) {
            var collisionSet = new Map();

            this.cells.forEach((val) => {
                var x_ = val[0] + dxn.dx
                var y_ = val[1] + dxn.dy
                pid = getPID(x_, y_, this.pidSize);
                collisionSet.set(pid, [x_, y_]);
            })

            this.collisionSets.push(collisionSet)
            dxn.turnLeft(1);
        }



        // Placeholder for setting different types/properties of this piece.
        this.type = 1
    }

    // Return whether or not the block has a collision with this angle.
    checkCollision(angle, board, boundarySets) {

        var [xSize, ySize] = [board.length, board[0].length];
        var collisionSet = this.collisionSets[angle];
        var boundarySet = boundarySets[angle];
        var collision = false;
        // Check for a boundary collision
        collisionSet.forEach((val) => {
            // BoundarySet has global PIDs
            // CollisionSet Local PID: add cx and cy to collisionSet to globalize
            var globalPid = getPID(val[0] + this.cx, val[1] + this.cy, this.pidSize)
            if (!collision && boundarySet.has(globalPid)) {
                collision = true;
            }
        })
        if (collision == true) {
            return true;
        }

        // Check for a board collision
        for (var y = 0; y < ySize; y++) {
            for (var x = 0; x < xSize; x++) {
                if (board[y][x] > 0) {
                    // x, y generate global PIDs
                    // Subtract cx and cy from PIDs to localize
                    var globalPid = getPID(x - this.cx, y - this.cy, this.pidSize)
                    if (!collision && collisionSet.has(globalPid)) {
                        return true;
                    }
                }
            }
        }

        return false;
    }

    // Move this piece based on a given x and y direction and recheck its appropriate hitbox
    activeMove(angle) {
        var dxn = new Direction(angle);
    }

    // Move this piece based on its gravity and recheck its appropriate hitbox.
    idleMove() {
        this.cx += this.dxn.dx;
        this.cy += this.dxn.dy;
    }
}

export default Piece;
