import Direction from "./gameUtils/Direction";
import Piece from "./gameUtils/Piece";

// TODO: Pull up this function into a utils/functions file
function randint(a, b) {
    return Math.floor(Math.random() * (b - a)) + a;
}

const Gravity = {
    RIGHT: 0,
    UP: 1,
    LEFT: 2,
    DOWN: 3,
}

const PIECE_SIZE = 5;
const SPAWN_OFFSET = 2;

const GameState = class {
    constructor (props) {
        this.timer = 0;
        this.emptyValue = 0; 
        this.board = [...Array(props.boardSize)].map(e => Array(props.boardSize).fill(this.emptyValue));
        this.boardSize = props.boardSize;
        this.pidSize = (props.boardSize + SPAWN_OFFSET * 2) * 2;
        this.gravity = new Direction(Gravity.DOWN);
        this.placeBlock = true;
        this.currPiece = null;
    }

    update() {
        if (this.placeBlock) {
            this.placeBlock = false;
            var [x, y] = [0, 0]

            // Place the current piece
            if (this.currPiece != null) {
                for (const cell in this.currPiece.cells) {
                    [x, y] = [cell[0] + this.currPiece.cx, cell[1] + this.currPiece.cy]
                    if (x >= 0 && x < this.boardSize && y >= 0 && y < this.boardSize) {
                        this.board[y][x] = this.currPiece.type
                    }
                }
            }

            // Set the position and gravity with which to create a new piece
            this.gravity.turnLeft(1);
            var r = randint(-SPAWN_OFFSET, this.boardSize + SPAWN_OFFSET)
            if (this.gravity.angle == Gravity.RIGHT) {
                [x, y] = [-SPAWN_OFFSET, r]
            } else if (this.gravity.angle == Gravity.UP) {
                [x, y] = [r, -SPAWN_OFFSET]
            } else if (this.gravity.angle == Gravity.LEFT) {
                [x, y] = [SPAWN_OFFSET + this.board_size, r]
            } else if (this.gravity.angle == Gravity.DOWN) {
                [x, y] = [r, SPAWN_OFFSET + this.board_size]
            }

            this.currPiece = new Piece({
                center_x: x,
                center_y: y,
                angle: this.gravity.angle,
                pieceSize: PIECE_SIZE,
                boardXSize: this.boardSize,
                boardYSize: this.boardSize,
                pidSize: this.pidSize,
            })
        } else {
            console.log(this.currPiece)
            if (this.currPiece) {
                this.currPiece.idleMove()
                if (this.currPiece.checkCollision(this.currPiece.angle, this.board)) {
                    this.placeBlock = true
                }
            }
        }
        return this;
    }

    // TODO: Call this.currPiece.activeMove and check for the collision in both idle and active directions
    executeAction(action) {
        return this;
    }
}

export default GameState;
