function getDiff(angle) {
    if (angle == 0) {
        return [1, 0]
    } else if (angle == 1) {
        return [0, -1]
    } else if (angle == 2) {
        return [-1, 0]
    } else if (angle == 3) {
        return [0, 1]
    }
}

class Direction {
    constructor(angle) {
        this.angle = angle;
        [this.dx, this.dy] = getDiff(angle);
    }

    turnLeft(n=1) {
        this.angle = (this.angle + n) % 4;
        [this.dx, this.dy] = getDiff(angle);
    }

    turnRight(n=1) {
        this.angle = (this.angle + 3 * n) % 4;
        [this.dx, this.dy] = getDiff(angle);;
    }

    turn(n) {
        if (n > 0) {
            this.turnLeft(n)
        } else {
            this.turnRight(n)
        }
    }
}

export default Direction;
