import {X, Y, GRID_SIZE, T, G0, G1, BASE, RAD} from './graphic-utils.js';

function drawRect(canvas, x, y, xSize, ySize) {
    canvas.beginPath();
    canvas.rect(x, y, x + xSize, y + ySize);
    canvas.stroke();
}

const DIMENSIONS = 800

const GameGraphics = (props) => {
    function render(canvas, board) {
        var [xSize, ySize] = [board[0].length, board.length]
        var [xCellSize, yCellSize] = [DIMENSIONS / xSize, DIMENSIONS / ySize]
        for (var y = 0; y < ySize; y++) {
            for (var x = 0; x < xSize; x++) {
                drawRect(canvas, x * xCellSize, y * yCellSize, xCellSize, yCellSize);
            }
        }
        console.log("rendered");
    }

    var canv = document.getElementById("gameGraphics");
    var ctx = canv != null ? canv.getContext('2d') : null;

    console.log("no hit", props.gameState, props)
    if (ctx != null && props.gameState != undefined) {
        console.log("hit")
        render(ctx, props.gameState.board);
    }
};

export default GameGraphics;
