import Direction from "./gameUtils/Direction";
import Piece from "./gameUtils/Piece";

// TODO: Pull up this function into a utils/functions file
function randint(a, b) {
    return Math.floor(Math.random() * (b - a)) + a;
}

const PIECE_SIZE = 5;
const SPAWN_OFFSET = 2;

const GameState = class {
    constructor ({boardSize}) {
        this.timer = 0;
        this.boardSize = boardSize;
        this.gravity = new Direction(3);
        this.placeBlock = true;
        this.currPiece= null;
    }

    update(increment) {
        if (this.placeBlock) {
            var [x, y] = [0, 0]
            r = randint(-SPAWN_OFFSET, this.boardSize + 2)

            this.gravity.turnLeft(1);
            if (this.gravity == 0) {
                [x, y] = [-2, r]
            } else if (this.gravity == 1) {
                [x, y] = [r, -2]
            } else if (this.gravity == 2) {
                [x, y] = [2 + this.board_size, r]
            } else if (this.gravity == 3) {
                [x, y] = [r, 2 + this.board_size]
            }
            self.currPiece = new Piece({
                center_x: x,
                center_y: y,
                angle: this.gravity,
                pieceSize: PIECE_SIZE,

            })
            this.placeBlock = false;
        }  
    }

    executeAction(action) {
        console.log(action);
        return this;
    }
}

export default GameState;
