    RIGHT: 0,
    UP: 1,
    LEFT: 2,
    DOWN: 3,
}

const PIECE_SIZE = 5;
const SPAWN_OFFSET = 2;

const GameState = class {
    constructor ({boardSize}) {
        this.timer = 0;
        this.boardSize = boardSize;
        this.gravity = Gravity.DOWN;
        this.placeBlock = true;
        this.currPiece= null;
    }

    update(increment) {
        if (this.placeBlock) {
