            r = randint(-SPAWN_OFFSET, this.boardSize + SPAWN_OFFSET)

            this.gravity.turnLeft(1);
            if (this.gravity == Gravity.RIGHT) {
                [x, y] = [-SPAWN_OFFSET, r]
            } else if (this.gravity == Gravity.UP) {
                [x, y] = [r, -SPAWN_OFFSET]
            } else if (this.gravity == Gravity.LEFT) {
                [x, y] = [SPAWN_OFFSET + this.board_size, r]
            } else if (this.gravity == Gravity.DOWN) {
                [x, y] = [r, SPAWN_OFFSET + this.board_size]
            }
            this.currPiece = new Piece({
                center_x: x,
                center_y: y,
                angle: this.gravity,
                pieceSize: PIECE_SIZE,

            })
            this.placeBlock = false;
        } 
    }

    executeAction(action) {
